import React, { Component } from 'react'

export default class AddPaintingForm extends Component {

    initialState = {
        name: '',
        type: '',
        dimensions: '',
        imgPath: '',
        price: null,
        medium: ''
    }

    state = this.initialState

    changeHandler = (event) => {
        const { key, val } = event.target;

        this.setState({
            [key]: val
        });

    }

    submitForm = (event) => {

    }

    render() {
        const painting = this.state
        return (
            <div className="small-container">
                <h2>Enter Painting Details</h2>
                <div className="flex-row">
                    <div className="flex-large">
                        <form onSubmit={this.submitForm}>
                            <input type="text" placeholder="Name"
                                value={painting.name}
                                onChange={this.changeHandler}
                                required />
                            <input className="flex-large" type="text" placeholder="Type"
                                value={painting.type}
                                onChange={this.changeHandler}
                                required />
                            <input type="text" placeholder="Medium"></input>
                            <input type="text" placeholder="Dimensions"></input>
                            <input type="text" placeholder="Image"></input>
                            <input type="text" placeholder="Price"></input>
                            <button type="submit" className="button">Add User</button>
                        </form>
                    </div>
                </div>
            </div >
        )
    }
}
