import React, { Component } from 'react'
import PaintingService from '../services/PaintingService'

export default class DisplayPaintings extends Component {

    constructor(props) {
        super(props)

        this.state = {
            paintings: []
        }

        this.allPaintings = this.allPaintings.bind(this);
    }

    allPaintings() {
        this.props.history.push('/add-single-painting');
    }

    componentDidMount() {
        PaintingService.getPaintings().then((response) => {
            this.setState({paintings : response.data})
        });
    }

    render() {
        return (
            <div>
                <button className="nav-link" onClick={this.allPaintings}>All Paintings<span className="sr-only">(current)</span></button>
                <h2 className="text"> Paintings Inventory </h2>
                <div>
                    <table className="striped-table">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Type</th>
                                <th>Medium</th>
                                <th>Dimensions</th>
                                <th>Image</th>
                                <th>Price</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.paintings.map(
                                    painting =>
                                    <tr key={painting.id}>
                                        <td>{painting.name}</td>
                                        <td>{painting.type}</td>
                                        <td>{painting.medium}</td>
                                        <td>{painting.dimensions}</td>
                                        <td>{painting.imgPath}</td>
                                        <td>{painting.price}</td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}
