import React, { Component } from 'react'

export default class AddPaintingForm extends Component {

    constructor(props) {
        super(props)

        this.state = {
            name: '',
            type: '',
            dimensions: '',
            imgPath: '',
            price: null,
            medium: ''
        }
    }

    changeHandler = (event) => {
        const { name, value } = event.target;

        this.setState({
            [name]: value
        });

    }

    render() {
        const painting = this.state
        return (
            <div className="small-container">
                <h2>Enter Painting Details</h2>
                <div className="flex-row">
                    <div className="flex-small">
                        <form>
                            <input type="text" placeholder="Name"
                                value={painting.name}
                                onChange={this.changeHandler}
                                required />
                            <input type="text" placeholder="Type"></input>
                            <input type="text" placeholder="Medium"></input>
                            <input type="text" placeholder="Dimensions"></input>
                            <input type="text" placeholder="Image"></input>
                            <input type="text" placeholder="Price"></input>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}
