import React, { Component } from 'react';
import { NavLink } from "react-router-dom";

export default class Header extends Component {

    constructor(props) {
        super(props)

        this.state = {

        }
        this.allPaintings = this.allPaintings.bind(this);
    }

    allPaintings() {
        this.props.history.push('/paintings');
    }

    render() {
        return (
            <div>
                <header>
                    <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                        <NavLink exact activeClassName="active" className="navbar-brand" to="/">Painting Inventory Management</NavLink>
                        <ul class="navbar-nav mr-auto">
                            <li class="nav-item active">
                                <NavLink activeClassName="active" className="nav-link" to="/paintings">All Paintings</NavLink>
                            </li>
                            {/* <li class="nav-item dropdown">
                                <NavLink activeClassName="active" className="nav-link dropdown-toggle" to="/paintings">Add Paintings</NavLink>
                                <div class="dropdown-menu" aria-labelledby="navbarDropdown">
                                    <NavLink activeClassName="active" className="dropdown-menu" to="/add-single-painting">Single Painting</NavLink>
                                    <NavLink activeClassName="active" className="dropdown-menu" to="/paintings">Upload a file</NavLink>
                                </div>
                            </li> */}
                            <Dropdown>
                                <Dropdown.Toggle variant="success" id="dropdown-basic">
                                    Dropdown Button
  </Dropdown.Toggle>

                                <Dropdown.Menu>
                                    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                                    <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                                    <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                                </Dropdown.Menu>
                            </Dropdown>
                        </ul>
                        <NavLink activeClassName="active" to="/contact">Contact</NavLink>
                    </nav>
                </header>
            </div>
        )
    }
}
